mapper.select:

SELECT COUNT(1)
  FROM basis_tb_job t
 WHERE t.state = 'U'


 AND t.user_id = #{userId} 


 AND t.`TYPE` = #{type} 


 AND t.job_id IN 
	#{code}

SELECT t.`JOB_ID` AS jobId, t.`USER_ID` AS userId, t.`JOB_CID` AS jobCId, t.`TITLE`, t.`CYCLE`, t.`SALARY`, t.`WORK_ADDRESS` AS workAddress, t.`JOB_NATURE` AS jobNature, t.`WORK_YEAR` AS workYear, t.`EDUCATION`, t.`REMARK`, t.`TYPE`, DATE_FORMAT(t.create_date, '%Y-%m-%d %T') AS createDate FROM basis_tb_job t WHERE t.state = 'U'
AND t.user_id = #{userId}
AND t.`TYPE` = #{type}
AND t.job_id IN #{code}
ORDER BY `CREATE_DATE` DESC LIMIT ${limit} OFFSET ${offset}

mapper.update:

UPDATE basis_tb_job t
   SET t.modify_date = NOW(),
	   t.modify_user = #{modifyUser}
 WHERE t.state = 'U'
   AND t.user_id = #{userId}
   AND t.job_id = #{jobId}


UPDATE basis_tb_job t
   SET t.modify_date = NOW(),
	   t.modify_user = #{modifyUser},
	   t.`TYPE` = #{type}
 WHERE t.state = 'U'
   AND t.`TYPE` <> #{type}
   AND t.user_id = #{userId}
   AND t.job_id = #{jobId}
