SELECT t.`JOB_ID` AS jobId, t.`USER_ID` AS userId, t.`TITLE`, t.`CYCLE`, t.`SALARY`, t.`WORK_ADDRESS` AS workAddress, t.`JOB_NATURE` AS jobNature, t.`WORK_YEAR` AS workYear, t.`EDUCATION`, t.`REMARK`, DATE_FORMAT(t.create_date, '%Y-%m-%d %T') AS createDate FROM basis_tb_job t WHERE t.state = 'U'
AND t.user_id = ${userId}
AND t.job_id IN #{code}
ORDER BY `CREATE_DATE` DESC LIMIT ${limit} OFFSET ${offset}
