mapper.update:

UPDATE basis_tb_resume_detail t
   SET t.modify_date = NOW(),
	   t.modify_user = #{modifyUser},
	   t.`JOB_CID` = #{jobCId},
	   t.`CYCLE` = #{cycle},
	   t.`CONTENT` = #{content},
	   t.`RANK` = #{rank}
 WHERE t.state = 'U'
   AND t.resume_id = #{resumeId}
   AND t.detail_id = #{detailId}
